contracts/apps/FaucetFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/Create2.sol";
import "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";
import "@account-abstraction/contracts/interfaces/IEntryPoint.sol";

import "./SimpleFaucet.sol";
import "./RedeemCodeFaucet.sol";

contract FaucetFactory {
    SimpleFaucet public immutable simpleFaucetImplementation;
    RedeemCodeFaucet public immutable redeemCodeFaucetImplementation;

    event SimpleFaucetCreated(address indexed faucet, address token, uint256 amount, uint48 redeemInterval);
    event RedeemCodeFaucetCreated(address indexed faucet, address token, uint256 amount, uint48 redeemInterval);

    constructor() {
        simpleFaucetImplementation = new SimpleFaucet();
        redeemCodeFaucetImplementation = new RedeemCodeFaucet();
    }


    function createSimpleFaucet(
        address owner,
        uint256 salt,
        IERC20Upgradeable _token, 
        uint256 _amount, 
        uint48 _redeemInterval, 
        address _redeemAdmin
    ) public returns (SimpleFaucet ret) {
        address addr = getSimpleFaucetAddress(owner, salt, _token, _amount, _redeemInterval, _redeemAdmin);

        emit SimpleFaucetCreated(addr, address(_token), _amount, _redeemInterval);

        uint codeSize = addr.code.length;
        if (codeSize > 0) {
            return SimpleFaucet(payable(addr));
        }
        ret = SimpleFaucet(
            payable(
                new ERC1967Proxy{salt: bytes32(salt)}(
                    address(simpleFaucetImplementation),
                    abi.encodeCall(SimpleFaucet.initialize, (owner, _token, _amount, _redeemInterval, _redeemAdmin))
                )
            )
        );
    }

    function getSimpleFaucetAddress(
        address owner,
        uint256 salt,
        IERC20Upgradeable _token, 
        uint256 _amount, 
        uint48 _redeemInterval, 
        address _redeemAdmin
    ) public view returns (address) {
        return
            Create2.computeAddress(
                bytes32(salt),
                keccak256(
                    abi.encodePacked(
                        type(ERC1967Proxy).creationCode,
                        abi.encode(
                            address(simpleFaucetImplementation),
                            abi.encodeCall(SimpleFaucet.initialize, (owner, _token, _amount, _redeemInterval, _redeemAdmin))
                        )
                    )
                )
            );
    }

    function createRedeemCodeFaucet(
        address owner,
        uint256 salt,
        IERC20Upgradeable _token, 
        uint48 _redeemInterval, 
        address _codeCreator
    ) public returns (RedeemCodeFaucet ret) {
        address addr = getRedeemCodeFaucetAddress(owner, salt, _token, _redeemInterval, _codeCreator);

        emit RedeemCodeFaucetCreated(addr, address(_token), 0, _redeemInterval);

        uint codeSize = addr.code.length;
        if (codeSize > 0) {
            return RedeemCodeFaucet(payable(addr));
        }
        ret = RedeemCodeFaucet(
            payable(
                new ERC1967Proxy{salt: bytes32(salt)}(
                    address(redeemCodeFaucetImplementation),
                    abi.encodeCall(RedeemCodeFaucet.initialize, (owner, _token, _redeemInterval, _codeCreator))
                )
            )
        );
    }

    function getRedeemCodeFaucetAddress(
        address owner,
        uint256 salt,
        IERC20Upgradeable _token, 
        uint48 _redeemInterval, 
        address _codeCreator
    ) public view returns (address) {
        return
            Create2.computeAddress(
                bytes32(salt),
                keccak256(
                    abi.encodePacked(
                        type(ERC1967Proxy).creationCode,
                        abi.encode(
                            address(redeemCodeFaucetImplementation),
                            abi.encodeCall(RedeemCodeFaucet.initialize, (owner, _token, _redeemInterval, _codeCreator))
                        )
                    )
                )
            );
    }
}

contracts/apps/RedeemCodeFaucet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";

/**
 * @title RedeemCodeFaucet
 * @dev A contract that allows users to redeem codes
 * It is upgradeable and has access control functionality.
 */
contract RedeemCodeFaucet is Initializable, OwnableUpgradeable, AccessControlUpgradeable, UUPSUpgradeable {
    bytes32 public constant REDEEM_CODE_CREATOR_ROLE = keccak256("REDEEM_CODE_CREATOR_ROLE");

    IERC20Upgradeable public token;
    uint48 public redeemInterval;
    address public codeCreator;

    /**
     * @dev Mapping to store the redeemableAmount for each redeemable code.
     */
    mapping(bytes32 codeHash => uint256 redeemableAmount) private redeemableAmounts;

    /**
     * @dev Mapping to store the validity period of each redeemable code.
     */
    mapping(bytes32 codeHash => uint48 validUntil) private validity;

    /**
     * @dev Mapping to store the time when each redeemable code was redeemed.
     */
    mapping(bytes32 codeHash => uint48 time) public redeemed;

    /**
     * @dev Mapping to store the time when each address redeemed from the faucet.
     */
    mapping(address sender => uint48 time) public lastRedeem;

    function initialize(address owner, IERC20Upgradeable _token, uint48 _redeemInterval, address _codeCreator) public initializer {
        __Ownable_init();
        __UUPSUpgradeable_init();
        token = _token;
        redeemInterval = _redeemInterval;
        codeCreator = _codeCreator;
        _setupRole(REDEEM_CODE_CREATOR_ROLE, _codeCreator);
        transferOwnership(owner);
    }

    /**
     * @dev Calculates the hash value for a given address and code.
     * @param _codeCreator The address of the code creator.
     * @param code The code to be hashed.
     * @return The calculated hash value.
     */
    function getHash(
        address _codeCreator,
        uint256 code
    ) public view returns (bytes32) {
        return
            keccak256(
                abi.encodePacked(
                    _codeCreator,
                    code,
                    block.chainid,
                    address(this)
                )
            );
    }

    /**
     * @dev Adds a redeem code to the faucet.
     * @param codeHash The redeem code hash to add.
     * @param redeemableAmount The redeemableAmount associated with the redeem code.
     * @param validUntil The expiration timestamp for the redeem code.
     * Requirements:
     * - `redeemableAmount` must be greater than zero.
     * - `validUntil` must be in the future.
     * - The redeem code must not have been already redeemed.
     */
    function addRedeemCode(bytes32 codeHash, uint256 redeemableAmount, uint48 validUntil) public onlyRole(REDEEM_CODE_CREATOR_ROLE) {
        require(
            redeemableAmount > 0,
            "RedeemCodeFaucet: redeemableAmount must be greater than zero"
        );

        uint48 currentTime = uint48(block.timestamp);

        require(
            validUntil > currentTime,
            "RedeemCodeFaucet: validUntil must be in the future"
        );

        require(
            redeemed[codeHash] == 0,
            "RedeemCodeFaucet: already redeemed"
        );

        redeemableAmounts[codeHash] = redeemableAmount;
        validity[codeHash] = validUntil;
    }


    /**
     * @dev Allows an address to redeem a code and receive tokens from the faucet.
     * @param code The code to redeem.
     * Requirements:
     * - The redeem interval must have passed since the last redemption by the caller.
     * - The code must not have been previously redeemed.
     * - The code must not have expired.
     * - The faucet must have sufficient balance of tokens.
     */
    function redeem(uint256 code) public {
        uint48 currentTime = uint48(block.timestamp);

        // stop a single address from redeeming too often
        require(
            lastRedeem[msg.sender] + redeemInterval < currentTime,
            "RedeemCodeFaucet: redeem interval not passed"
        );

        bytes32 codeHash = getHash(
                    codeCreator,
                    code
                );

        require(
            redeemed[codeHash] == 0,
            "RedeemCodeFaucet: code already redeemed"
        );

        require(
            currentTime < validity[codeHash],
            "RedeemCodeFaucet: code expired"
        );

        require(
            token.balanceOf(address(this)) >= redeemableAmounts[codeHash],
            "RedeemCodeFaucet: insufficient balance"
        );

        uint256 redeemableAmount = redeemableAmounts[codeHash];

        token.transfer(msg.sender, redeemableAmount);

        redeemed[codeHash] = currentTime;
        lastRedeem[msg.sender] = currentTime;
    }

    /**
     * @dev Allows the redeem code creator to withdraw all the tokens from the contract.
     */
    function withdraw() public onlyRole(REDEEM_CODE_CREATOR_ROLE) {
        token.transfer(codeCreator, token.balanceOf(address(this)));
    }

    function grantRole(bytes32 role, address account) public virtual override onlyRole(getRoleAdmin(role)) {
        require(
            role != REDEEM_CODE_CREATOR_ROLE,
            "AccessControl: cannot grant codeCreator role"
        );

        _grantRole(role, account);
    }

    function _authorizeUpgrade(
        address newImplementation
    ) internal view override onlyOwner {
        (newImplementation);
    }
}

contracts/apps/SimpleFaucet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";

/**
 * @title SimpleFaucet
 * @dev A simple faucet contract that allows users to redeem tokens at a specified interval.
 */
contract SimpleFaucet is Initializable, OwnableUpgradeable, AccessControlUpgradeable, UUPSUpgradeable {
    bytes32 public constant REDEEM_ADMIN_ROLE = keccak256("REDEEM_ADMIN_ROLE");

    IERC20Upgradeable public token;
    uint256 public amount;
    uint48 public redeemInterval;
    address public redeemAdmin;

    /**
     * @dev A mapping that keeps track of the last time each address redeemed from the faucet.
     * The key of the mapping is the address of the sender, and the value is the timestamp (in seconds) when the redemption occurred.
     */
    mapping(address receiver => uint48 time) public redeemed;

    function initialize(address owner, IERC20Upgradeable _token, uint256 _amount, uint48 _redeemInterval, address _redeemAdmin) public initializer {
        __Ownable_init();
        __UUPSUpgradeable_init();
        token = _token;
        amount = _amount;
        redeemInterval = _redeemInterval;
        redeemAdmin = _redeemAdmin;
        _setupRole(REDEEM_ADMIN_ROLE, _redeemAdmin);
        transferOwnership(owner);
    }

    /**
     * @dev Allows users to redeem tokens from the SimpleFaucet contract.
     * Users can redeem tokens based on a redeem interval or if they have not redeemed before.
     * If a redeem interval is set, the user must wait until the interval has passed since their last redemption.
     * If no interval is set, the user must not have redeemed before.
     * Upon successful redemption, the specified amount of tokens is transferred to the user's address.
     * The redemption time is recorded for future interval checks.
     */
    function redeem() public {
        uint48 currentTime = uint48(block.timestamp);

        if (redeemInterval > 0) {
            // if a redeem interval is set, check if the user can redeem based on interval
            uint48 allowedTime = redeemed[msg.sender] + redeemInterval;

            require(
                currentTime >= allowedTime,
                "SimpleFaucet: redeem interval not passed"
            );
        } else {
            // if no interval is set, check if the user has redeemed before
            require(
                redeemed[msg.sender] == 0,
                "SimpleFaucet: already redeemed"
            );
        }

        require(
            token.balanceOf(address(this)) >= amount,
            "SimpleFaucet: insufficient balance"
        );

        token.transfer(msg.sender, amount);

        redeemed[msg.sender] = currentTime;
    }

        /**
     * @dev Allows the redeem code creator to withdraw all the tokens from the contract.
     */
    function withdraw() public onlyRole(REDEEM_ADMIN_ROLE) {
        token.transfer(redeemAdmin, token.balanceOf(address(this)));
    }

    function _authorizeUpgrade(
        address newImplementation
    ) internal view override onlyOwner {
        (newImplementation);
    }
}

